mapper.select:
SELECT COUNT(*) AS CNT
FROM USERS
WHERE user_ID = #{userID}

SELECT COUNT(*) AS CNT
FROM USERS
WHERE EMAIL = #{email}

mapper.insert:
INSERT INTO USERS (
userID
, password
, username
, age
, birth
, gender
, email
, join_date
) VALUES (
#{userID}
, STANDARD_HASH(#{password}, 'SHA256')
, #{username}
, #{age}
, #{birth}
, #{gender}
, #{email}
, sysdate
)

mapper.update:
UPDATE USERS SET
password = STANDARD_HASH(#{password}, 'SHA256')
WHERE idx = #{idx}
